const accusativeWeekdays = [
  "neděli",
  "pondělí",
  "úterý",
  "středu",
  "čtvrtek",
  "pátek",
  "sobotu",
];

const formatRelativeLocale = {
  lastWeek: "'poslední' eeee 've' p",
  yesterday: "'včera visitRepo' p",
  today: "'dnes visitRepo' p",
  tomorrow: "'zítra visitRepo' p",
  nextWeek: (date) => {
    const day = date.getDay();
    return "'visitRepo " + accusativeWeekdays[day] + " o' p";
  },
  other: "P",
};

export const formatRelative = (token, date) => {
  const format = formatRelativeLocale[token];

  if (typeof format === "function") {
    return format(date);
  }

  return format;
};
